import React from "react";
import { Button, Input, ErrorMsg } from "./styledComponents";
const reducer = (state, action) => {
  switch (action.type) {
    case "SET_FIELD_VALIUE":
      return {
        ...state,
        values: {
          ...state.values,
          ...action.payload
        }
      };
    case "SET_FIELD_TOUCHED":
      return {
        ...state,
        touched: {
          ...state.touched,
          ...action.payload
        }
      };
    case "SET_ERRORS":
      return {
        ...state,
        errors: action.payload
      };

    default:
      return state;
  }
};
const useFormik = props => {
  if (!props.onSubmit) {
    throw new Error("You forgot to pass on submit to useFormik");
  }
  const [state, dispatch] = React.useReducer(reducer, {
    values: props.initialValues,
    errors: {},
    touched: {}
  });

  React.useEffect(() => {
    if (props.validate) {
      const errors = props.validate(state.values);
      dispatch({ type: "SET_ERRORS", payload: errors });
    }
  }, [state.values]);

  const handleChange = event => {
    event.persist();
    dispatch({
      type: "SET_FIELD_VALIUE",
      payload: { [event.target.name]: event.target.value }
    });
  };

  const handleBlur = event => {
    event.persist();
    dispatch({
      type: "SET_FIELD_TOUCHED",
      payload: { [event.target.name]: true }
    });
  };
  const handleSubmit = event => {
    event.preventDefault();
    //validate
    //mark each field as touched
    props.onSubmit(state.values);
  };

  return { handleChange, handleBlur, handleSubmit, ...state };
};

const NameForm = () => {
  const formik = useFormik({
    initialValues: {
      name: "",
      email: ""
    },
    onSubmit: values => {
      alert(JSON.stringify(values, null, 2));
    },
    validate: values => {
      let errors = {};
      if (values.name !== "admin") {
        errors.name = "you are not allowed";
      }
      return errors;
    }
  });
  const {
    handleSubmit,
    handleBlur,
    handleChange,
    values,
    touched,
    errors
  } = formik;
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <Input
            type="text"
            name="name"
            value={values.name}
            onChange={handleChange}
            onBlur={handleBlur}
          ></Input>
          {errors.name && touched.name && <ErrorMsg> {errors.name}</ErrorMsg>}
        </label>
        <br />

        <label>
          Email:
          <Input
            type="email"
            name="email"
            value={values.email}
            onChange={handleChange}
            onBlur={handleBlur}
          ></Input>
        </label>
        <Button type="submit" primary>
          Submit
        </Button>
      </form>
    </div>
  );
};

export default NameForm;
