export const isObject = obj => {
  if (obj !== null && typeof obj == "object") {
    return false;
  }
  return true;
};

export function setNestedObjectValues(
  object,
  value,
  visited = new WeakMap(),
  response = {}
) {
  for (let k of Object.keys(object)) {
    const val = object[k];
    if (isObject(val)) {
      if (!visited.get(val)) {
        visited.set(val, true);
        response[k] = Array.isArray(val) ? [] : {};
        setNestedObjectValues(val, value, vidited, response[k]);
      } else {
        response[k] = value;
      }
    }
    return response;
  }
}
